  //  setVideos( videos.filter(video=>video.id!==id)
  //  );

  // }

  function editVideo(id) {
    console.log("editVideo");
    setEditableVideo(videos.find((video) => video.id === id));
  }

  // function updateVideos(video){
  //   dispatch({type:'UPDATE',payload:video});
